    public class AccountItemSeeder : IDataSeeder
    {
        public int Order => 15;
        public string Name => "會計科目表";

        public async Task SeedAsync(AppDbContext context)
        {
            if (await context.AccountItems.AnyAsync())
                return;

            var seedData = GetSeedData();
            var codeToEntity = new Dictionary<string, AccountItem>();

            // 第一階段：建立所有 AccountItem（不含 ParentId）
            foreach (var (code, name, enName, desc, enDesc, level, parentCode, accountType, direction, isDetail, sortOrder) in seedData)
            {
                var item = new AccountItem
                {
                    Code = code,
                    Name = name,
                    EnglishName = enName,
                    Description = desc,
                    EnglishDescription = enDesc,
                    AccountLevel = level,
                    AccountType = accountType,
                    Direction = direction,
                    IsDetailAccount = isDetail,
                    SortOrder = sortOrder,
                    Status = EntityStatus.Active,
                    CreatedAt = DateTime.Now,
                    CreatedBy = "System"
                };

                codeToEntity[code] = item;
                await context.AccountItems.AddAsync(item);
            }

            // 先儲存以取得自動產生的 Id
            await context.SaveChangesAsync();

            // 第二階段：設定 ParentId
            foreach (var (code, _, _, _, _, _, parentCode, _, _, _, _) in seedData)
            {
                if (parentCode != null && codeToEntity.ContainsKey(parentCode) && codeToEntity.ContainsKey(code))
                {
                    codeToEntity[code].ParentId = codeToEntity[parentCode].Id;
                }
            }

            await context.SaveChangesAsync();
        }

